function reloadTable() {
    let tableId = $("table:first").attr("id");
    $("#" + tableId).DataTable().ajax.reload()
}
var showCallBackData = function() {
    $("#id").val(""), $(".ajaxForm")[0].reset(), $("#form-modal-id").modal("hide"), reloadTable()
};

function onDelete(e) {
    var action_url = $(e).data("route");
    Swal.fire({
        title: "Are you sure to remove the exclude airline code ?",
        showCancelButton: !0,
        confirmButtonText: "Yes"
    }).then((result => {
        result.isConfirmed && $.ajax({
            url: action_url,
            type: "DELETE",
            success: function(res) {
                Swal.fire(res.message, "", res.success ? "success" : "warning"), reloadTable()
            },
            error: function() {
                Swal.fire("Changes are not saved", "", "info")
            }
        })
    }))
}
$(document).ready((function() {
    "use strict";
    $.ajaxSetup({
        headers: {
            "X-CSRF-TOKEN": $('meta[name="csrf-token"]').attr("content")
        }
    }), $(".show-modal").on("click", (function() {
        $("show-modal input").val("");
        $(this).attr("data-add-route");
        $("#form-modal-id").modal("show")
    }))
}));