@php
    function convertMinutesToHoursMinutes($minutes) {
        $hours = floor($minutes / 60);
        $remainingMinutes = $minutes % 60;

        if ($hours == 0) {
            return "{$remainingMinutes}min";
        } elseif ($remainingMinutes == 0) {
            return "{$hours}hr";
        } else {
            return "{$hours}hr {$remainingMinutes}min";
        }
    }
